export const loadCommAction =(idPost) =>{
    return(dispatch)=>{
        async function getComment(id){
            const comments=await CommentService.getAll(id)
            return comments
        }


        //? провести соответствие, что title поста соответсвует idPost  
        //и принирмать title а передавать  в getComment(idPost) уже
        const comments=getComment(idPost)
        console.log(comments)
        comments.then(comments=>
            dispatch({type:"LOAD_COMM",payload:comments}))

    }
}

export const addCommAction =(body,email,name,title,id=0,postId=0,) =>{
