const LOAD_POST = "LOAD_POST";
const ADD_POST = "ADD_POST";
const  DELETE_POST="DELETE_POST";

const defaultState = {
    posts: []
}




export default (state=defaultState, {type,payload}) => {
    switch(type) {
        case LOAD_POST: {
            return {posts:payload}
        }
        case ADD_POST: {
            console.log(payload)
            console.log(state);
            let {userId}=payload
            userId=[...state.posts].map((post,idx)=>{userId=idx+1})
            console.log(userId)//номер дліни массіва увелічівается
            //console.log(oldPosts)//undefined
            return {posts:[...state.posts,payload]}
        }
        case DELETE_POST: {
            const filterPosts=[...state.posts].filter(p=>!p.title.startsWith(payload)) 
            return {posts:filterPosts}
        }
        default: {
            return state
        }
    }
}
