const LOAD_COMM = "LOAD_COMM";

const defaultState = {
    comms: []
}

export default (state=defaultState, {type,payload}) => {
    switch(type) {
        case LOAD_COMM: {
            return {comms:payload}
        }
        case ADD_COMM: {
            const{aboutcomm,...title}=payload
            return {comms:payload}
        }
        default: {
            return state
        }
    }
}
