export const loadCommAction =() =>{
    return(dispatch)=>{
        async function getComment(){// иногда сразу async  getComment()
            const resp=await fetch ("https://jsonplaceholder.typicode.com/comments")//axios.get
            const comments=await resp.json()
            return comments
        }
        const comments=getComment()
        console.log(comments)
        comments.then(comments=>
            dispatch({type:"LOAD_COMM",payload:comments}))

    }
}
