import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Route, Routes } from 'react-router';
import { loadPostAction } from './store/actions/postAction';
import { loadCommAction } from './store/actions/commAction';

const App = () => {
	const dispatch = useDispatch();
	const state = useSelector((state) => state);
	console.log(state);
	const [titleArticle, setTitleArticle] = useState('');
	/*
  return (
		<Routes>
			<Route path= />
		</Routes>
	);
  */
	return (
		<div>
			<button onClick={() => dispatch(loadPostAction())}>getPosts</button>
			<button onClick={() => dispatch(loadCommAction())}>
				getComments
			</button>
			<input
				placeholder="ввести назание статьи"
				onChange={(e) => setTitleArticle(e.target.value)}
			></input>
			<button onClick={() => dispatch(addPostAction())}>getPosts</button>
			<input placeholder="ввести тело статьи"></input>
		</div>
	);
};

export default App;

